UPDATE auth_register_user
SET email_status = 1
WHERE token=#{token}

UPDATE auth_register_user
SET token = #{token}
WHERE username = #{username}

UPDATE auth_register_user
SET icon = #{icon}
WHERE id = #{id}

UPDATE auth_register_user
SET password = #{password}
WHERE username = #{username}

UPDATE auth_register_user
SET password = #{password}
WHERE id = #{userId}
