mapper.update:
UPDATE IGNORE live_channel
SET url=#{url}, rtmp_url=#{rtmpUrl}, rtmp_key = #{rtmpKey}, playUrl=#{playUrl}
WHERE userId=#{userId}

UPDATE live_channel
SET title=#{title}, message=#{message}
WHERE userId=#{userId}

UPDATE live_channel
SET title=#{title}
WHERE userId=#{userId}

UPDATE live_channel
SET message=#{message}
WHERE userId=#{userId}

UPDATE live_channel
SET price=#{price}
WHERE userId=#{userId}

UPDATE live_channel
SET title=#{title}, message=#{message}, price=#{price}, rating = #{rating}
WHERE userId=#{userId}

UPDATE live_channel
SET available=1, startTime=now()
WHERE userId=#{userId}

UPDATE live_channel
SET available=0
WHERE userId=#{userId}

UPDATE live_channel
SET preview=#{preview}
WHERE userId=#{userId}
