mapper.update:
UPDATE IGNORE bvision_channel_live
SET url=#{url}, rtmp_url=#{rtmpUrl}, rtmp_key = #{rtmpKey}, playUrl=#{playUrl}
WHERE userId=#{userId}

UPDATE bvision_channel_live
SET title=#{title}, message=#{message}
WHERE userId=#{userId}

UPDATE bvision_channel_live
SET title=#{title}
WHERE userId=#{userId}

UPDATE bvision_channel_live
SET message=#{message}
WHERE userId=#{userId}

UPDATE bvision_channel_live
SET price=#{price}
WHERE userId=#{userId}

UPDATE bvision_channel_live
SET link=#{link}
WHERE userId=#{userId}

UPDATE bvision_channel_live
SET title=#{title}, message=#{message}, link=#{link}, price=#{price}, rating = #{rating}
WHERE userId=#{userId}

UPDATE bvision_channel_live
SET available=1, startTime=now()
WHERE userId=#{userId}

UPDATE bvision_channel_live
SET available=0
WHERE userId=#{userId}

UPDATE bvision_channel_live
SET preview=#{preview}
WHERE userId=#{userId}
