mapper.update:
UPDATE IGNORE bvision_channel_live_purchase
  SET expires_time = #{expiresTime}
    WHERE channel_id = #{channelId} AND viewer_id = #{viewerId}

mapper.select:
SELECT id, channel_id , viewer_id, expires_time, create_time, modify_time
  FROM bvision_channel_live_purchase
    WHERE channel_id = #{channelId} AND viewer_id = #{viewerId}
